INSERT INTO `find_info` VALUES ('其他寻人', '574964', '李云寿', '男', '1910年10月13日', '170厘米左右', '1942年10月13日', '20世纪40年代', '32岁', '河南省', '山东省', '不详', '妻于氏，长子李乃俭，次子乃*（如果曾祖留有后人，请回答。）曾祖云寿公，时因家贫，自言闯关东后不知所踪，其时已有二子，家祖父和二祖父，后二祖早夭，家祖父三十余岁早逝，其时家父尚幼，所知不多。后文革期间，村中有人传言有人到村头询问家祖父名讳，因村中年轻者多有不知，回答说没有此人，等家父知道后前去寻找，来人已远走。于是村中老人怀疑乃曾祖之后人来寻，家父也以此为憾事。综上原因，无法考证曾祖离家时年月，只能估摸当时应为山东省牟平或海阳亦或牟海县管辖，村名冷格庄。现为山东。曾祖如果留有后人，应该是“乃”字辈，以前写作“迺”字，其次是“书”或者“树”字辈，至我是“家”字辈，而后是“声”字辈。', NULL);
